{"changed":true,"filter":false,"title":"app.rb","tooltip":"/techstagram/app.rb","value":"require 'bundler/setup'\nBundler.require\nrequire 'sinatra/reloader' if development?\nrequire './models.rb'\n# dotsenv\nrequire 'dotenv/load'\n# cloudinary\nrequire 'cloudinary'\nrequire 'cloudinary/uploader'\nrequire 'cloudinary/utils'\n\nbefore do\n  Dotenv.load\n  Cloudinary.config do |config|\n    config.cloud_name = ENV['CLOUD_NAME']\n    config.api_key = ENV['CLOUDINARY_API_KEY']\n    config.api_secret = ENV['CLOUDINARY_API_SECRET']\n    config.secure = true\n  # パスからpage_nameを指定する\n  path = request.fullpath.split(\"/\").last\n  case path\n  when \"newpost\" then\n    @page_name = \"newpost\"\n  else\n    @page_name = \"home\"\n  end\nend\nend\n\nget'/' do \n  @posts = Post.order(created_at: :desc)\n  erb :index\nend\n\nget \"/newpost\" do\n  erb :newpost\nend\n\npost '/newpost' do\n  if params[:upload_photo]\n    image = params[:upload_photo]\n    tempfile = image[:tempfile]\n    upload = Cloudinary::Uploader.upload(tempfile.path)\n    img_url = upload['url']\n  else\n    img_url = url(\"img/user_img_nil.png\")\n  end \n  \n post = Post.create(\n   image_url: img_url,\n   icon_image: params[:icon_image],\n   user_name: params[:user_name],\n   caption: params[:caption],\n   location: params[:location]\n  )\n  redirect '/'\nend \n\nget '/:id/edit' do\n  @post = Post.find(params[:id])\n  erb :editpost\nend \n\npost'/:id/edit' do\n  post = Post.find(params[:id])\n  if params[:upload_photo]\n    image = params[:upload_photo]\n    tempfire = image[:tempfile]\n    upload = Cloudinary::Upload(tempdfire.path)\n    img_url = upload['url']\n  else\n    img_url = post.image_url\nend\n\npost.update(\n  image_url: img_url,\n  icon_image: params[:icon_image],\n  user_name: params[:user_name],\n  caption: params[:caption],\n  location: params[:location],\n  like: 0\n)\nredirect '/'\nend\n\nget '/:id/delete' do\n  post = Post.find(params[:id])\n  post.destroy\n  redirect '/'\nend\n\npost 